class SCADA{constructor(){this.data={},this.events=[],this.src="img/symbols/"}async read(t={}){this.data=t;for(let t of this.events)t()}async onSymbolStatus(t="",e="",s=""){this.symbolStatus(t,e,this.data[s])}async onDisplay(t="",e=""){this.display(t,this.data[e])}async onSymbolDisplay(t="",e="",s=""){this.symbolDisplay(t,e,this.data[s])}async onChangeColor(t="",e="",s,a){this.changeColor(t,this.data[e],s,a)}async symbolStatus(t="",e,s){let a;switch(typeof s){case"number":a=s;break;case"boolean":a=!0===s?1:0;break;default:a=0}let l=this.src+e+"_"+a+".png";document.getElementById(t).src=l}async display(t="",e){document.getElementById(t).style.display=!1===e||0===e?"none":"block"}async symbolDisplay(t,e,s){!1===s||0===s?(document.getElementById(t).style.display="block",document.getElementById(e).style.display="none"):(document.getElementById(t).style.display="none",document.getElementById(e).style.display="block")}async changeColor(t="",e,s,a){document.getElementById(t).style.background=!1===e||0===e?a:s}async setTop(t="",e){document.getElementById(t).style.top=e+"px"}async setLeft(t="",e){document.getElementById(t).style.left=e+"px"}} //export default SCADA;
class Timer {constructor(pt) {this.sp=pt*20;this.et=0;this.q = false;}run(en){ if(en){this.et++;this.q=(this.et>=this.sp);}else{this.et=0;this.q=false;}}}